"use strict";

const test = require('ava');

const {
  decode
} = require('ini');

const {
  getFileFromContainer
} = require('./util');

const getFactomdConf = async config => decode((await getFileFromContainer('factomd.conf', {
  network: 'main',
  ...config
})));

const optionNameTest = (config, yamlName, optionName, optionValue) => test(`${yamlName} -> ${optionName}`, async t => {
  const factomdConf = await getFactomdConf(config);
  t.is(factomdConf.app[optionValue], optionValue);
});

optionNameTest({
  apiPassword: 'password',
  apiUser: 'username'
}, 'apiPassword', 'FactomdRpcPass', 'password');
